import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';

// Fix for default marker icon in React-Leaflet
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';

let DefaultIcon = L.icon({
  iconUrl: icon,
  shadowUrl: iconShadow,
  iconSize: [25, 41],
  iconAnchor: [12, 41]
});

L.Marker.prototype.options.icon = DefaultIcon;

// Auto-center component to keep ISS in view
function AutoCenter({ position }) {
  const map = useMap();

  useEffect(() => {
    if (position) {
      map.setView(position, map.getZoom());
    }
  }, [position, map]);

  return null;
}

function ISSMap() {
  const [issPosition, setIssPosition] = useState(null);
  const [pathHistory, setPathHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch ISS data
  const fetchData = async () => {
    try {
      setError(null);

      // Get current ISS position
      const currentResponse = await axios.get('http://localhost:5001/api/iss/current');
      const lat = currentResponse.data.latitude;
      const lon = currentResponse.data.longitude;
      setIssPosition([lat, lon]);

      // Get 5-hour history for path
      const historyResponse = await axios.get('http://localhost:5001/api/iss/history-hours?hours=5');

      // Convert to coordinate pairs [lat, lon] and reverse (newest first)
      const path = historyResponse.data
        .map(pos => [pos.latitude, pos.longitude])
        .reverse(); // Reverse so oldest position is first in line

      setPathHistory(path);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching map data:', err);
      setError('Failed to load map data');
      setLoading(false);
    }
  };

  // Fetch data on mount and every 10 seconds
  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 10000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div style={{
        backgroundColor: '#f0f0f0',
        padding: '20px',
        borderRadius: '8px',
        marginBottom: '20px',
        height: '400px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{ textAlign: 'center' }}>
          <h2 style={{ marginTop: 0 }}>üó∫Ô∏è Loading World Map...</h2>
          <p>Fetching ISS location data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{
        backgroundColor: '#ffebee',
        padding: '20px',
        borderRadius: '8px',
        marginBottom: '20px',
        border: '1px solid #ef9a9a'
      }}>
        <h2 style={{ marginTop: 0, color: '#c62828' }}>‚ö†Ô∏è Map Error</h2>
        <p>{error}</p>
      </div>
    );
  }

  if (!issPosition) {
    return (
      <div style={{
        backgroundColor: '#f0f0f0',
        padding: '20px',
        borderRadius: '8px',
        marginBottom: '20px'
      }}>
        <p>No ISS data available yet...</p>
      </div>
    );
  }

  return (
    <div style={{
      backgroundColor: '#f0f0f0',
      padding: '20px',
      borderRadius: '8px',
      marginBottom: '20px',
      position: 'relative'
    }}>
      <h2 style={{ marginTop: 0, marginBottom: '15px' }}>
        üó∫Ô∏è ISS World Map - Live Tracking
      </h2>

      <div style={{
        height: '400px',
        borderRadius: '6px',
        overflow: 'hidden',
        border: '2px solid #ddd'
      }}>
        <MapContainer
          center={issPosition}
          zoom={3}
          style={{ height: '100%', width: '100%' }}
          scrollWheelZoom={true}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* Auto-center on ISS */}
          <AutoCenter position={issPosition} />

          {/* ISS Path (last 5 hours) */}
          {pathHistory.length > 0 && (
            <Polyline
              positions={pathHistory}
              color="#0066ff"
              weight={3}
              opacity={0.7}
            />
          )}

          {/* ISS Current Position Marker */}
          <Marker position={issPosition}>
            <Popup>
              <strong>üõ∞Ô∏è ISS Current Location</strong><br />
              <strong>Latitude:</strong> {issPosition[0].toFixed(4)}¬∞<br />
              <strong>Longitude:</strong> {issPosition[1].toFixed(4)}¬∞<br />
              <em>Updates every 10 seconds</em>
            </Popup>
          </Marker>
        </MapContainer>
      </div>

      {/* Legend */}
      <div style={{
        marginTop: '10px',
        fontSize: '14px',
        color: '#666',
        display: 'flex',
        gap: '20px',
        alignItems: 'center'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <div style={{
            width: '12px',
            height: '12px',
            backgroundColor: '#ff0000',
            borderRadius: '50%',
            border: '2px solid #fff',
            boxShadow: '0 0 4px rgba(0,0,0,0.3)'
          }}></div>
          <span>Current ISS Position</span>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <div style={{
            width: '30px',
            height: '3px',
            backgroundColor: '#0066ff',
            opacity: 0.7
          }}></div>
          <span>Last 5 Hours Path</span>
        </div>
        <div style={{ marginLeft: 'auto', fontStyle: 'italic' }}>
          {pathHistory.length} position records
        </div>
      </div>
    </div>
  );
}

export default ISSMap;
